dataTableObject =  function(dataTableObject, columns, data, buttons) {
    // Get libraries
    const Moment = global.get('moment');
    const MomentRange = global.get('momentrange');
    const moment = MomentRange.extendMoment(Moment)
    var momentTimezone = global.get('moment_timezone');
    var lodash = global.get('lodash');
    var _ = global.get('underscore');
    var BSON = global.get('bson');
    var ObjectID = BSON.ObjectID;
    var accounting = global.get('accounting'); 
    var accountingoptions = {	symbol : "€",	decimal : ".", 	thousand: " ", 	precision : 0, 	format: "%s %v"}; // example = Output = accounting.formatMoney(Number, accountingoptions );


    // validate inputs

    if ( typeof dataTableObject !== 'object'                ||
            dataTableObject.hasOwnProperty("gadgetId")      ||
            dataTableObject.hasOwnProperty("gadgetName")    ||
            dataTableObject.hasOwnProperty("gadgetType")    ||
            dataTableObject.hasOwnProperty("gadgetData")    ||
            dataTableObject.hasOwnProperty("gadgetOptions") ){
        return dataTableObject
    }
    else if ( Array.isArray(columns) === false){
        return dataTableObject
    }
    else if ( Array.isArray(data) === false ){
        return dataTableObject
    }
    else {
    var tableColumnData  =  { 
         "columnHead" : columns,
          "columnOptions": {
            "columnStyle": {
              "width": "30%"
            },
            "cellStyle": {
            //   "color": "red"
            },
            "allowSort": true
          }
        };

    // generate rows
    var tableRowData = []
            _.each(data, function(dataItem, dataIndex){

                obj = {
                       "rowData" :[],
                               "rowOptions": {
                               //   "state": "warning",
                                 "globalClick": true,
                                 "style": {
                                   "width": "20%"
                                 }
                               },
                       }
                 _.each(columns, function(columnsItem, columnsIndex){
                        if ( columnsItem.hasOwnProperty('headKey') && dataItem.hasOwnProperty(columnsItem.headKey)
                            ){
                              obj.rowData.push({
                                   "data"    : dataItem[columnsItem.headKey],
                                   "type"    : typeof(dataItem[headKey]),
                                   "headKey" : columnsItem.headKey,
                                   "hidden"  : false,
                                   "editable": false
                              })
                            }
            })

            tableRowData.push(obj)
        })

    // define paged value
    if (tableRowData.length > 10 ){
        paged = 10;
    } else {
        paged = tableRowData.length;
    }

    dataTableObject.gadgetOptions = {
                                          "allowSearch": true,
                                          "maxItems": paged,
                                          "striped": true,
                                          "export": true
                                        };
    dataTableObject.gadgetData.tableColumnData  =  tableColumnData;
    dataTableObject.gadgetData.tableRowData     =  tableRowData;
    // dataTableObject.gadgetData.tableButton =  tableButton;


    return dataTableObject
  }

    }
global.set("functions.dataTableObject" ,dataTableObject)


is_dir = function(path) {
     var fs = global.get('fsextra');
                            try {
                                var stat = fs.lstatSync(path);
                                return stat.isDirectory();
                            } catch (e) {
                                // lstatSync throws an error if path doesn't exist
                                return false;
                            }
                        }
global.set("functions.is_dir" ,is_dir)


syncDirlist = function(path) {

            if (path.substr(path.length - 1) !== "/" ){
                path = path +"/"
            }

            var _ = global.get('underscore');
            var fs = global.get('fsextra');
            var responseObj = { 
                                    path : path,
                                    parent:{
                                        filePath : path,
                                        absolutePath : path,
                                        isDirectory : global.get("functions.is_dir")(path)
                                    },
                                }
            if (!responseObj.parent.isDirectory){
                  responseObj.parent.isFile = true
                 responseObj.parent.type = "file" 
            } else if ( responseObj.parent.isDirectory){
                responseObj.parent.type = "dir" 
            }



        if (responseObj.parent.isDirectory){
            responseObj.child = []
            _.each(fs.readdirSync(responseObj.parent.absolutePath), function(item, index){
                if (item.substr(0,1) !== "."){


                Obj ={
                    filePath : item,
                    absolutePath : responseObj.parent.absolutePath+item +"/",
                    isDirectory : global.get("functions.is_dir")(responseObj.parent.absolutePath+item +"/"),
                }

                if (!Obj.isDirectory){
                     Obj.isFile = true
                     Obj.type = "file" 
                } else if ( Obj.isDirectory){
                    Obj.type = "dir" 
                    // Obj.child = global.get("functions.syncDirlist")(Obj.absolutePath)
                }

                responseObj.child.push(Obj)

                }
            })
        } 
        return responseObj;
}

global.set("functions.syncDirlist" ,syncDirlist)

