formatFunction = function(inner){
    if (inner.length > -1 ){
        var i;
        for (i = 0; i < inner.length; i++) {
             if (inner[i].inner.length > -1){
                        inner[i].inner = global.get("functions.formatFunction")(inner[i].inner)
                    } 
            if (inner[i].keys.length > -1){
                var x;
                for (x = 0; x < inner[i].keys.length; x++) {
                            var ObjectKeys = Object.keys(inner[i].keys[x].values)
                            var y;
                            for (y = 0; y < ObjectKeys.length; y++) {
                                 if (
                                     ObjectKeys[y] !== "Amount" && 
                                     ObjectKeys[y] !== "Number" &&
                                     ObjectKeys[y] !== "Transactions" &&
                                     ObjectKeys[y] !== "Categories" &&
                                     ObjectKeys[y] !== "Work packages" &&
                                     ObjectKeys[y] !== "Projects" &&
                                     ObjectKeys[y] !== "Project" &&
                                     ObjectKeys[y] !== "Memberships" &&
                                     ObjectKeys[y] !== "Employee's" &&
                                     ObjectKeys[y] !== "Users" &&
                                     ObjectKeys[y] !== "Events" &&
                                     ObjectKeys[y] !== "To do" &&
                                     ObjectKeys[y] !== "Processed" &&
                                     ObjectKeys[y] !== "FTE" &&
                                     isNaN(inner[i].keys[x].values[ObjectKeys[y]]) === false){
                                   inner[i].keys[x].values[ObjectKeys[y]] = accounting.formatMoney(inner[i].keys[x].values[ObjectKeys[y]], accountingoptions );
                                } else  if ((ObjectKeys[y] === "Amount" || ObjectKeys[y] === "Number") && isNaN(inner[i].keys[x].values[ObjectKeys[y]]) === false){
                                    inner[i].keys[x].values[ObjectKeys[y]] = Math.round(Number(inner[i].keys[x].values[ObjectKeys[y]])*100)/100;
                                }
                            }
                }
            } 
        }
    }
    return inner;
};

global.set("functions.formatFunction" ,formatFunction)


