formOptions =  function(array, key, value, type, groupKey,  groupKeyArray, groupKeyArrayKey, groupKeyArrayValue) { 

        var lodash = global.get('lodash');
        var _ = global.get('underscore');
        var Moment = global.get('moment');
        var MomentRange = global.get('momentrange');
        var moment = MomentRange.extendMoment(Moment);

        if (Array.isArray(array) === false ||  array.length <= 0){
          output =  "array is not an array or has no elements";  
        }

        else if (key === undefined || typeof key !== 'string'){
               output =  "key must be present and a string";
        }
        else if (value === undefined || typeof value !== 'string'){
               output = "value must be present and a string";
           }
        else if (type === undefined || typeof type !== 'string' || (type !== 'select' &&  type !== 'opt-group')){
               output = "type must be present and a string && === select || === opt-group";} 
        else {


              if (type === 'select'){
                   output = {};

                   _.each(array, function(Item, Index){

                        if (Item.hasOwnProperty(key) && Item.hasOwnProperty(value) ){
                          output[Item[key]] = Item[value];
                        } else {
                            output["ERROR " + Index] = Item
                        }
                   })
                   return output;
              } 
              else if (type === 'opt-group') {

                    if (Array.isArray(groupKeyArray) ){

                        if ((groupKeyArrayKey === undefined || typeof groupKeyArrayKey !== 'string') || (groupKeyArrayValue === undefined || typeof groupKeyArrayValue !== 'string')  ){
                             output =  "If groupKeyArray presented, groupKeyArrayKey & groupKeyArrayValue is needed and should be type STRING";
                        } else {

                            groupedArray = lodash.groupBy(array, groupKeyArrayKey);
                            output = {};
                            _.each(array , function(Item, Index){

                                if (output.hasOwnProperty(groupedArray[Item[groupKey][0][groupKeyArrayValue]]) === false){
                                    output[groupedArray[Item[groupKey][0][groupKeyArrayValue]]] = {};
                                }

                                output[groupedArray[Item[groupKey][0][groupKeyArrayValue]]][Item.key] = Item.value;
                            });
                        }
               }
           }
        }

}
global.set("functions.formOptions" ,formOptions)

fileManagement =  function(source, destination, sessionId, operation) {

        var fs = global.get('fsextra');
        var _ = global.get('underscore');

        if (!sessionId ){
            return "Required data not present (sessionId)"
        }

        // check if file exists
        else if (!fs.existsSync(source)){
            return "Invalid source"
        }


        else if (!operation || operation === "copySync"){

                if (!source||!destination){
                     return "Required data not present (source OR destination) "
                } 
                else {
                            if (destination.slice(0,1) === "/"){
                                newDestination = destination.slice(1)
                                destination = newDestination
                            }
                            destinationFolder = ""
                            _.each(destination.split("/"), function(item , Index){
                                if (Index +1 !== destination.split("/").length ){
                                    destinationFolder =  destinationFolder + "/" + item
                                }
                            })


                            fs.mkdirsSync(String(global.get("DomainProperties.rootFolder") + global.get("DomainProperties.filesFolder") +  sessionId +  "/TEMP/" + destinationFolder).replace(/\/{2}/g, "/"));

                            fs.copySync(source, String(global.get("DomainProperties.rootFolder") + global.get("DomainProperties.filesFolder") +  sessionId +  "/TEMP/" + destination).replace(/\/{2}/g, "/"))

                            return global.get("DomainProperties.RootDomain") +    String(global.get("DomainProperties.filesFolder") +  sessionId +  "/TEMP/" + destination).replace(/\/{2}/g, "/")

                } 

        }
                else if (!operation || operation === "copySyncCore"){

                if (!source||!destination){
                     return "Required data not present (source OR destination) "
                } 
                else {
                            if (destination.slice(0,1) === "/"){
                                newDestination = destination.slice(1)
                                destination = newDestination
                            }



                            fs.copySync(source, global.get("DomainProperties.publicFolder") + global.get("DomainProperties.filesFolder") +  sessionId +  "/TEMP/" + destination)

                            return global.get("DomainProperties.filesFolder") +  sessionId +  "/TEMP/" + destination

                }

        }

        else if (operation === "emptyDirSync")
                {
                    fs.emptyDirSync(global.get("DomainProperties.publicFolder") + global.get("DomainProperties.filesFolder") +sessionId + "/TEMP")
                     return {state : 'success', message: "emptyDirSync - " + global.get("DomainProperties.publicFolder") + global.get("DomainProperties.filesFolder") +sessionId + "/TEMP"+ " - Success"}
                   }
        else if (operation === "removeSync")
                {
                    fs.removeSync(global.get("DomainProperties.publicFolder") + global.get("DomainProperties.filesFolder") +sessionId)
                    return {state : 'success', message: "removeSync - " + global.get("DomainProperties.publicFolder") + global.get("DomainProperties.filesFolder") +sessionId + "/TEMP"+ " - Success"}

                }


        else if (operation === "initSessionFolder"){

            fs.ensureDirSync(global.get("DomainProperties.publicFolder") + global.get("DomainProperties.filesFolder") )
            fs.ensureDirSync(global.get("DomainProperties.publicFolder") + global.get("DomainProperties.filesFolder") + sessionId)
            // fs.ensureDirSync(global.get("DomainProperties.publicFolder") + sessionId + "/SystemFiles" )
            fs.ensureDirSync(global.get("DomainProperties.publicFolder") + global.get("DomainProperties.filesFolder") + sessionId+ "/TEMP" )

            fs.copySync(global.get("DomainProperties.rootPath") + "/SystemFiles" , global.get("DomainProperties.publicFolder") +global.get("DomainProperties.filesFolder") + "/SystemFiles" )

        }



}
global.set("functions.fileManagement" ,fileManagement)


formatTime = function (time) {
    var _ = global.get('underscore');
    //split startTime on ":"
    arr = time.split(":");

    newTime = ""

    _.each(arr, function(Item, index){
        if (Item.length === 1){
            newTime = newTime + "0"+Item
        } else (
            newTime = newTime +Item
        )

        if (index === 0){
           newTime = newTime + ":" 
        }

    })

     return newTime;
}

global.set("functions.formatTime" ,formatTime)
