// filter work_package items

group = lodash.groupBy(Childs, "ParentId");
groupedPayload= []

_.each(group.undefined, function(parentValue, parentIndex){
    if (group.hasOwnProperty(parentValue.organization_id)){

        // check if childs are active
        _.each(group[parentValue.organization_id], function(Item, Index){
            if (Item.State === "Active"){
                parentValue.State = Item.State;
            }
        });
        parentValue.inner.unshift({
            State : parentValue.State,
            name: "sub_organizations",
            label: "Sub organizations",
            group: true,
            iconUrl : global.get("DomainProperties.prodUrl") + "/files/SystemFiles/icons/icon_organization.svg", // global.get("icons.fas.organization"),
             filePath :global.get("icons.images.organization") ,
            inner:lodash.orderBy(group[parentValue.organization_id], ['name','bubble' ], ['asc', 'desc'])
            })
    }
    groupedPayload.push(parentValue)
})








