var deviceId = msg.sessionNew.deviceId ; 
var revotioSessionId = msg.sessionNew.revotioSessionId ;


if (        msg.sessionNew.hasOwnProperty("timestamp") && 
        moment().diff(moment(msg.sessionNew.timestamp) > global.get("SystemVars.responseTimeout"))){



        payload = {
            deviceId: deviceId,
            revotioSessionId: revotioSessionId,
            gadgetsList :msg.sessionNew.Gadgets,
            Type: "Update",
            TimeOut : true

        }

        headers = {
            'Access-Control-Allow-Origin': '*',
            'Access-Control-Allow-Methods': 'PUT,GET,POST,DELETE,OPTIONS',
            'Access-Control-Allow-Headers': 'Content-Type',
            'content-type': 'application/json',
        };


    updateObj = {
                    query : {
                            query :{_id:new ObjectID(msg.sessionNew._id)}, 
                            update : { "$set" : {Gadgets : [],
                                                timestamp: new Date(),
                                                responseSend : false
                            }},
                            options : {upsert: true}
                            },
                    connection:global.get("DB_data.connection"),
                    database:global.get("DB_data.appDB"),
                    collection:'sessions',
                    operation:'update', // updateMany , updateOne
                    }

            mongodbTools.resolveAsync(updateObj, node, function(res){
            }) 

        response = {
            req : msg.req,
            res : msg.res,
            payload : payload,
            headers : headers,
        }

        sessionUpdate = {RevotioData : msg.RevotioData}


        } else if (msg.sessionNew.hasOwnProperty("timestamp")) {
            msg.time = moment().diff(moment(msg.sessionNew.timestamp))

            if ( global.get("SystemVars").hasOwnProperty("delaySize") ){
                 msg.delay = global.get("SystemVars.delaySize") 
            }

            delay = msg;
        }


return [delay, response, sessionUpdate];

