stylesUpdate = function(array) {

   _.each(array, function(arrayItem, arrayIndex){
       if (arrayItem.hasOwnProperty("styles") &&
       typeof arrayItem.styles === 'object'
       ){
           array[arrayIndex].styles_old = arrayItem.styles
           oldWidth = array[arrayIndex].styles.width
           delete  array[arrayIndex].styles
           array[arrayIndex].styles = {
               width : oldWidth
           }
       }

       if ( arrayItem.hasOwnProperty("columns") &&
            Array.isArray(arrayItem.columns)  &&
            arrayItem.columns.length >  0)
            {
               array[arrayIndex].columns =   stylesUpdate( array[arrayIndex].columns)
            }

        if ( arrayItem.hasOwnProperty("container") &&
            Array.isArray(arrayItem.container)  &&
            arrayItem.container.length >  0)
            {
               array[arrayIndex].container =   stylesUpdate( array[arrayIndex].container)
            }
        if ( arrayItem.hasOwnProperty("content") &&
            Array.isArray(arrayItem.content)  &&
            arrayItem.content.length >  0)
            {
               array[arrayIndex].content =   stylesUpdate( array[arrayIndex].content)
            }
        if ( arrayItem.hasOwnProperty("content") &&
            typeof arrayItem.content === "object"  &&
            arrayItem.content.hasOwnProperty("container") &&
            Array.isArray(arrayItem.content.container)  &&
            arrayItem.content.container.length >  0
            )
            {
               array[arrayIndex].content.container =   stylesUpdate( array[arrayIndex].content.container)
            }

            if ( arrayItem.hasOwnProperty("customContent") &&
            Array.isArray(arrayItem.customContent)  &&
            arrayItem.customContent.length >  0)
            {
               array[arrayIndex].customContent =   stylesUpdate( array[arrayIndex].customContent)
            }

             if ( arrayItem.hasOwnProperty("screenProfiles") &&
            typeof arrayItem.screenProfiles === "object" ){
                _.each(Object.keys(arrayItem.screenProfiles), function(key, index){
                    if (arrayItem.screenProfiles[key].hasOwnProperty("content") &&
                        arrayItem.screenProfiles[key].content.hasOwnProperty("container") &&
                        Array.isArray(arrayItem.screenProfiles[key].content.container)  &&
                        arrayItem.screenProfiles[key].content.container.length >  0 )
                        {
                           array[arrayIndex].screenProfiles[key].content.container =   stylesUpdate( array[arrayIndex].screenProfiles[key].content.container)
                        }
                })

            }  
   })



   return array
};

msg.oldPayload = msg.payload

msg.payload = stylesUpdate(msg.oldPayload)



return msg

